-- Insert Vietnamese translations for the new posts
ON CONFLICT (blog_post_id, language_code) DO NOTHING;

-- Display the results
SELECT 'Added sample posts successfully!' as message;
SELECT COUNT(*) as total_posts FROM blog_posts WHERE status = 'published';
SELECT COUNT(*) as total_translations FROM blog_post_translations; 